import React, { useState } from 'react'


const AddExpenseForm = ({createExpense, expenses}) => {

    const [title, setTitle] = useState('');
    const [amount, setAmount] = useState('')
    const [category, setCategory] = useState('')

    const handleTitleChange = event => setTitle(event.target.value);
    const handleAmountChange = event => setAmount(event.target.value)
    const handleCategoryChange = event => setCategory(event.target.value)

    const handleSubmit = event => {
        event.preventDefault();
        console.log(event);
        createExpense({

            title: title,
            amount: amount,
            category: category
        })
        setTitle('');
        setAmount('');
        setCategory('');

    }
    const categories = [{
        ENTERTAINMENT: "Entertainment"},
        {BILLS : "Bills"},
       {EATING_OUT : "Eating Out"},
       {TRANSPORT : "Transport"}
    //    RENT : "Rent",
    //    MORTGAGE: "Mortgage",
    //    GROCERIES : "Groceries",
    //    SUBSCRIPTIONS: "Subscriptions",
    //    OTHER : "Other"
];



    const expenseOptions = categories.map((categoryItem, index) => {
        return <option key={index} value={index}>{categoryItem}</option>
    })




  return (
    <>
    <div>AddExpenseForm</div>
    <form onSubmit={handleSubmit}>
        <h1>Add Expense</h1>
        <div>
            <label htmlFor='title'>Name:</label>
            <input type="text" id="title" value={title} required onChange={handleTitleChange}></input>
        </div>
        <div>
            <label htmlFor='amount'>Amount:</label>
            <input id="amount" value={amount} required onChange={handleAmountChange}></input>
        </div>
        <div>
            <select name="Select Category" onChange={handleCategoryChange}value={category}>
               <option>Select Category</option>
                {expenseOptions}
            </select>
        </div>
        <button type="submit">Add Expense</button>
    </form>

    </>
  )
}

export default AddExpenseForm
